async function cleanupOldServices\(\) {
  // The 6 valid service IDs we want to keep
  const validServiceIds = [
    ''service-civil-construction'',
    ''service-interior-architecture'',
    ''service-mep-engineering'',
    ''service-manufacturing-joinery'',
    ''service-fit-out-execution'',
    ''service-handover-maintenance'',
  ];

  const allServices = await client.fetch\(''*[_type == ""service""]{ _id, title }''\);
  console.log\(''Current services:'', allServices.length\);

  const toDelete = allServices.filter\(s => !validServiceIds.includes\(s._id\)\);
  console.log\(''Services to delete:'', toDelete.length\);

  for \(const service of toDelete\) {
    const title = typeof service.title === ''object'' ? service.title.en : service.title;
    try {
      await client.delete\(service._id\);
      console.log\(''Deleted:'', title\);
    } catch \(error\) {
      console.error\(''Failed to delete'', title + '':'', error.message\);
    }
  }

  // Verify final state
  const remaining = await client.fetch\(''*[_type == ""service""] | order\(order asc\) { _id, title, slug, order }''\);
  console.log\(''\\nFinal services in Sanity:''\);
  console.log\(''=''.repeat\(40\)\);
  remaining.forEach\(\(s, i\) => {
    const title = typeof s.title === ''object'' ? s.title.en : s.title;
    console.log\(\(i+1\) + ''. '' + title + '' \(/'' + s.slug?.current + ''\)''\);
  }\);
  console.log\(''\\nTotal:'', remaining.length\);
}

cleanupOldServices\(\);
